import React, { useContext } from "react";
import UserService from "./../00-Context/UserService";
import axios from "axios";
import TableRow from "./TableRow/TableRow";

const Cart = () => {
  const userService = useContext(UserService);

  var products = [];
  var tableRows = [];

  if (userService.username.length > 0) {
    let urlBuilder =
      "https://itpro2017.herokuapp.com/api/users/" +
      userService.username +
      "/cart-products";
    axios
      .get(urlBuilder)
      .then(response => {
        products = response.data;
      })
      .catch(error => {
        console.log(error);
      });
  }
  tableRows = tableRows = products.map(item => {
    return <TableRow id={item.id} image={item.image} title={item.title} />;
  });

  console.log(tableRows);

  return (
    <div className="container">
      <table className=" text-center table table-striped table-dark">
        <thead>
          <tr>
            <th scope="col">ID</th>
            <th scope="col">Image</th>
            <th scope="col">Title</th>
            <th scope="col">{""}</th>
          </tr>
        </thead>
        <tbody>{tableRows}</tbody>
      </table>
    </div>
  );
};

export default Cart;
