import React, { Component, useContext } from "react";
import { Link } from "react-router-dom";
import CartService from "./../../00-Context/CartService";

class CartPresentation extends Component {
  constructor() {
    super();
    this.state = { cartNumber: 0 };
  }

  static contextType = CartService;

  updateMe = () => {
    this.setState();
  };

  render() {
    console.log(this.context.itemBasketCount);
    return (
      <Link className="navbar-brand cart-bg px-2" to="/cart">
        <i className="fas fa-shopping-basket mr-2"></i>
        {this.state.cartNumber} Items
      </Link>
    );
  }
}

export default CartPresentation;
