import React, { Component } from "react";
import UserService from "./../../00-Context/UserService";

class UsernamePresentation extends Component {
  constructor() {
    super();
    this.state = { username: "" };
  }

  static contextType = UserService;

  updateMe = () => {
    this.setState({ username: this.context.username });
  };

  render() {
    return (
      <li className="ml-3 nav-item">
        <div className="form-inline my-2 my-lg-0">
          <input
            className="form-control mr-sm-2"
            type="text"
            placeholder="Username"
            onChange={this.props.usernameChangeHandler}
            value={this.props.username}
          />
        </div>
      </li>
    );
  }
}

export default UsernamePresentation;
