import React, { useState } from "react";
import axios from "axios";

var products = [];
axios
  .get("https://itpro2017.herokuapp.com/api/products")
  .then(response => {
    products = response.data;
  })
  .catch(error => {
    console.log(error);
  });

const ProductInfo = props => {
  console.log(products);

  return (
    <div>
      <div className="row">
        <div className="col-12 col-md-6">{props.image}</div>
        <div className="col-4 col-md-2">
          <h5 className="card-title text-right">Title:</h5>
          <p className="card-text text-right">About:</p>
          <p className="card-title text-right">Price:</p>
          <p className="card-text text-right">
            <small>Items In Stock:</small>
          </p>
        </div>
        <div className="col-8 col-md-4">
          <h5 className="card-title">
            {products[props.match.params.id - 1].title}
          </h5>
          <p className="card-text">
            {products[props.match.params.id - 1].description}
          </p>
          <p className="card-title">
            {products[props.match.params.id - 1].price}
          </p>
          <p className="card-text">
            <small>{products[props.match.params.id - 1].quantity}</small>
          </p>
        </div>
      </div>
      <div className="row">
        <a href={"/merch/"} className="btn btn-light m-3">
          ADD TO CART
        </a>
        <a href={"/merch/"} className="btn btn-light m-3">
          BACK
        </a>
      </div>
    </div>
  );
};

export default ProductInfo;
