import React, { useState, useContext } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import UserState from "./../../00-Context/UserService";
import CartContext from "./../../00-Context/CartService";
import ProductInfoPresentation from "./ProductInfoPresentation/ProductInfoPresentation";

const ProductInfo = props => {
  var product = {};
  console.log(props.price);

  const cartService = useContext(CartContext);
  const userService = useContext(UserState);

  const getProductUrl =
    "https://itpro2017.herokuapp.com/api/products/" + props.match.params.id;

  axios
    .get(getProductUrl)
    .then(response => {
      product = response.data;
      console.log("eiksikt");
    })
    .catch(error => console.log(error));

  const postToCartUrl =
    "https://itpro2017.herokuapp.com/api/users/" +
    userService.username +
    "/cart-products";
  const addToCart = () => {
    axios
      .post(postToCartUrl, {
        id: Number(product.id),
        image: product.image,
        title: product.title
      })
      .then(() => {
        cartService.updateMe();
      })
      .catch(error => console.log(error));
  };

  return (
    <div>
      <ProductInfoPresentation />
    </div>
  );
};

export default ProductInfo;
