import React, { useContext, useState } from "react";
import UserService from "./../00-Context/UserService";
import axios from "axios";

const Cart = () => {
  var [cartState, setCartState] = useState({
    username: localStorage.getItem("username")
  });
  const products = [];
  const userService = useContext(UserService);
  let username = userService.username;

  let urlBuilder =
    "https://itpro2017.herokuapp.com/api/users/" +
    cartState.username +
    "/cart-products";
  axios
    .get(urlBuilder)
    .then(response => {
      setCartState(response.data.length);
    })
    .catch(error => {
      console.log(error);
    });
  return <h1>Cart</h1>;
};

export default Cart;
