import React, { Component, useContext } from "react";
import UserService from "./../../00-Context/UserService";

class UsernamePresentation extends Component {
  constructor() {
    super();
    this.state = { cartNumber: 0 };
  }

  updateMe = () => {
    let { cartService } = useContext(UserService);
    this.setState({ cartNumber: cartService });
  };

  render() {
    console.log(this.context.itemBasketCount);
    return (
      <li className="ml-3 nav-item">
        <div className="form-inline my-2 my-lg-0">
          <input
            className="form-control mr-sm-2"
            type="text"
            placeholder="Username"
            onChange={this.updateMe}
            value={userContext.username}
          />
        </div>
      </li>
    );
  }
}

export default UsernamePresentation;
