import React, { Component, useContext } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import UserState from "./../../00-Context/UserService";
import CartContext from "./../../00-Context/CartService";

class ProductInfo extends Component {
  constructor() {
    super();
    this.state = { title: "", description: "", price: "" };
    const getProductUrl =
      "https://itpro2017.herokuapp.com/api/products/" +
      this.props.match.params.id;
    axios
      .get(getProductUrl)
      .then(response => {
        this.setProduct(response.data);
        console.log(response.data);
      })
      .catch(error => console.log(error));
  }

  cartService = useContext(CartContext);
  userService = useContext(UserState);

  postToCartUrl =
    "https://itpro2017.herokuapp.com/api/users/" +
    this.userService.username +
    "/cart-products";

  addToCart = () => {
    axios
      .post(this.postToCartUrl, {
        id: Number(this.state.id),
        image: this.state.image,
        title: this.state.title
      })
      .then(() => {
        this.cartService.updateMe();
      })
      .catch(error => console.log(error));
  };

  render() {
    return (
      <div>
        <div className="row">
          <div className="col-12 col-md-6">
            <img
              src="https://images.unsplash.com/photo-1518302057166-c990a3585cc3?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1267&q=80"
              className="card-img-top"
              alt="Unable to load"
            />
          </div>
          <div className="col-4 col-md-2">
            <h5 className="card-title text-right">Title:</h5>
            <p className="card-text text-right">About:</p>
            <p className="card-title text-right">Price:</p>
            <p className="card-text text-right">
              <small>Items In Stock:</small>
            </p>
          </div>
          <div className="col-8 col-md-4">
            <h5 className="card-title">{this.state.title}</h5>
            <p className="card-text">{this.state.description}</p>
            <p className="card-title">{this.state.price}</p>
            <p className="card-text">
              <small>{this.state.quantity}</small>
            </p>
            <div className="row">
              <button onClick={this.addToCart} className="btn btn-light m-3">
                ADD TO CART
              </button>
              <Link to={"/merch/"} className="btn btn-light m-3">
                BACK
              </Link>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default ProductInfo;
