import React, { useContext, useState } from "react";
import UserService from "./../00-Context/UserService";
import CartService from "./../00-Context/CartService";
import Table from "./Table/Table";

const Cart = () => {
  const userService = useContext(UserService);
  const cartService = useContext(CartService);
  var [url, setUrl] = useState(
    "https://itpro2017.herokuapp.com/api/users/" +
      userService.username +
      "/cart-products"
  );

  refreshComponents = () => {
    axios
      .get(this.props.url)
      .then(response => {
        this.products = response.data;
      })
      .catch(error => {
        console.log(error);
      });
  };

  var updateCart = () => {
    setUrl(
      "https://itpro2017.herokuapp.com/api/users/" +
        userService.username +
        "/cart-products"
    );
  };

  console.log("update me: " + cartService.updateMe());

  cartService.updateCartList = updateCart;

  return (
    <div>
      <Table url={url} username={userService.username} />
    </div>
  );
};

export default Cart;
