import React, { useState } from "react";
import axios from "axios";

const ProductInfo = props => {
  console.log(props);
  var [product, setProducts] = useState({ product: {} });
  axios
    .get("https://itpro2017.herokuapp.com/api/products")
    .then(response => {
      setProducts({ product: response[props.match.params.id - 1] });
    })
    .catch(error => {
      console.log(error);
    });
  return (
    <div>
      <div className="row">
        <div className="col-12 col-md-6">{props.image}</div>
        <div className="col-4 col-md-2">
          <h5 className="card-title text-right">Title: {props.title}</h5>
          <p className="card-text text-right">About: {props.description}</p>
          <p className="card-title text-right">Price: {props.price}</p>
          <p className="card-text text-right">
            <small>Items In Stock: {props.quantity}</small>
          </p>
        </div>
        <div className="col-8 col-md-4">
          <h5 className="card-title">{props.title}</h5>
          <p className="card-text">{props.description}</p>
          <p className="card-title">{props.price}</p>
          <p className="card-text">
            <small>{props.quantity}</small>
          </p>
        </div>
      </div>
      <div className="row">
        <a href={"/merch/"} className="btn btn-light m-3">
          ADD TO CART
        </a>
        <a href={"/merch/"} className="btn btn-light m-3">
          BACK
        </a>
      </div>
    </div>
  );
};

export default ProductInfo;
