import React, { Component } from "react";
import "./Nav.css";
import NavHeader from "./NavHeader/NavHeader";
import UserService from "./../00-Context/UserService";
import CartService from "./../00-Context/CartService";

class NavBar extends Component {
  constructor() {
    super();
    this.state = { username: "", cartItems: 0 };
  }

  static contextType = UserService;

  usernameChangeHandler = event => {
    event.preventDefault();
    this.setState({ username: event.target.value });
    console.log(this.state.username);
  };

  componentDidMount() {
    if (this.state.username.length === 0) {
      this.setState({
        username: localStorage.getItem("username"),
        itemBasketCount: 0
      });
    }
  }

  render() {
    return (
      <div className="fill-window">
        <CartService.Provider value={{ cartItemsCount: "asdf" }}>
          <UserService.Provider
            value={{ username: this.state.username, updateme: () => {} }}
          >
            <NavHeader
              username={this.state.username}
              usernameChangeHandler={this.usernameChangeHandler}
              itemBasketCount={this.state.itemBasketCount}
            />
            <div className="container mt-3 p-3">{this.props.children}</div>
          </UserService.Provider>
        </CartService.Provider>
      </div>
    );
  }
}

export default NavBar;
