import React, { useState, useContext } from "react";
import { Link } from "react-router-dom";
import CartService from "./../../00-Context/CartService";
import UserService from "./../../00-Context/UserService";
import axios from "axios";

const CartPresentation = props => {
  var [cartState, setCartState] = useState({ itemsCount: 0 });

  let cartContext = useContext(CartService);
  let userService = useContext(UserService);
  cartContext = { updateme: "something went good" };
  console.log(cartContext);

  var updateCart = () => {
    let username = userService.username;
    let urlBuilder = axios.get(
      "https://itpro2017.herokuapp.com/api/users/" + username + "/cart-products"
    );
    console.log("username: " + username);
    axios
      .get(urlBuilder)
      .then(response => {
        console.log(response);
      })
      .catch(error => {
        console.log(error);
      });
  };

  updateCart();

  return (
    <Link className="navbar-brand cart-bg px-2" to="/cart">
      <i className="fas fa-shopping-basket mr-2"></i>
      {cartState.itemsCount} Items
    </Link>
  );
};

export default CartPresentation;
